div.script:
var nnpview=3;
var nsp=30;
  if($(document).width()<=980){
	 var nnpview = 3; 
	 var nsp=30;
  }
  if($(document).width()<=768){
	 var nnpview = 2; 
	 var nsp=20;
  }
 
  if($(document).width()<=480){
	 var nnpview = 1; 
	 var nsp=10;
  }
  var mySwiper = new Swiper('#pagehome',{
	pagination: '',
	paginationClickable: true,
	slidesPerView: nnpview,
	loop: true,
	speed: 400,
	autoplay:6000,
	spaceBetween: nsp,
	nextButton: '.scroll-next-page',
	prevButton: '.scroll-prev-page'
	
  })

        var pview = 6;
        if ($(document).width() <= 980) {
            pview = 5;
            $('#parner').height(75);
        }
        if ($(document).width() <= 768) {
            pview = 5;
            $('#parner').height(75);
